import React from 'react';
import Student from '../../../dto/StudentDTO';
import swal from 'swal';

export default class AddStudentForm extends React.Component {
    constructor(props) {

        this.state = {
            id: "",
            coachName:"",
            firstGen:false,
            race:"",
            disablility:false,
            classification:"",
            sevenTargetSchools:"",
            notifiedStudent:false,
            scholarshipMatchingComplete:false,
            scholarshipEssay:false,
            scholarshipDeadlines:"",
            scholarshipEssay3:"",
            reviewOfEssay:"",
            completedFasfsa:false,
            admissionDeadlines:"",
            rejected:false,
            waitlisted:false,
            accepted:false,
            collegePacketComplete:false,
            coachFinalReview:"",
            lor:"",
            resume:false,
            interview:false,
            award:0
        };
    }

    addStudent = async () => {

        let _student = new Student.Student(this.state._id, this.state.coachName,
            this.state.race, this.state.disablility, this.state.classification,
            this.state.sevenTargetSchools, this.state.notifiedStudent, this.state.scholarshipMatchingComplete,
            this.state.scholarshipEssay, this.state.scholarshipDeadlines, this.state.scholarshipEssay3,
            this.state.reviewOfEssay, this.state.completedFasfsa, this.state.admissionDeadlines,
            this.state.rejected, this.state.waitlisted, this.state.accepted, this.state.collegePacketComplete,
            this.state.coachFinalReview, this.state.lor, this.state.resume,
            this.state.resume, this.state.award);

            let config = {
            method: "POST",
            body: JSON.stringify(_student),
            headers: {
                "Content-Type":"application/json",
            }
        }

        let ajax = await fetch('/api/add', config);
        if (ajax.status === 500) throw Error();

        return await ajax.json();

    };

    onFieldChange(e, fieldName) {
        this.setState({fieldName: e.target.value});
    }

    render(e) {
        return (
            <div id="newStudentForm">
                <input type="text" value="" onChange={this.onFieldChange.bind(this, e, "id")} />
                <input type="text" value="" onChange={this.onFieldChange.bind(this, e, "coachName")} />
                <input type="text" value="" onChange={this.onFieldChange.bind(this, e, "firstGen")} />
                <button type="button" />
            </div>
        )
    }
}
