import React from 'react';
import ReactDOM from 'react-dom';
import ContextMenu from './ContextMenu';

export default class StudentList extends React.Component {
    constructor(props) {
        super(props);
        console.log("StudentList Constructor");
        this.state = {
            students: this.props.Students || []
        };

    }

    update() {
        let config = {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "admin"
            }
        };
        fetch('/api/students', config)
            .then(res => res.json())
            .then(res => this.setState({ students: res }));
    }

    componentDidMount() {
        this.update();
    }

    componentDidUpdate(prevStudents) {
        if (this.props.Students.length !== prevStudents.Students.length)
            this.update();
    }

    showContextMenu(e, student) {
        let container = document.createElement("div");
        container.id = "context-container";
        document.body.appendChild(container);
        console.log(e.target.clientX);
        ReactDOM.render(<ContextMenu X={e.clientX} Y={e.clientY} SelectedStudent={student}/>, 
            container);
    }

    render() {
        let list = this.state.students.map(student => {
            return <div className="student-list-item" key={student.StudentId + "_t"} id={student.StudentId}>
                <span className="student-list-item-label" key={student.StudentId + "_m"}>{student.studentId}</span>
                <div className="options-button" key={student.StudentId + "_b"} onClick={e => this.showContextMenu(e, student)}>
                    <div></div>
                    <div></div>
                    <div></div>
                </div>
            </div>
        });

        return <div className="student-container">
            {list}
        </div>

    }
}
