import React from 'react';
import Field from './AddStudentField';

export default class AddStudentForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            student: {
                id: this.props.Student.StudentId || "",
                coach_name: this.props.Student.CoachName || "",
                first_gen: this.props.Student.FirstGen || "",
                race: this.props.Student.Race || "",
                disablility: this.props.Student.Disablility || "",
                classification: this.props.Student.Classification || "",
                seven_target_schools: this.props.Student.SevenTargetSchools || "",
                notified_student: this.props.Student.NotifiedStudent || "",
                scholarship_matching_complete: this.props.Student.ScholarshipMatchingComplete || "",
                scholarship_essay: this.props.Student.ScholarshipEssay || "",
                scholarship_deadlines: this.props.Student.ScholarshipDeadlines || "",
                scholarship_essay_3: this.props.Student.ScholarshipEssay3 || "",
                review_of_essay: this.props.Student.ReviewOfEssay || "",
                completed_fasfsa: this.props.Student.CompletedFafsa || "",
                admission_deadlines: this.props.Student.AdmissonDealines || "",
                rejected: this.props.Student.Rejected || "",
                waitlisted: this.props.Student.Waitlisted || "",
                accepted: this.props.Student.SevenTargetSchools || "",
                college_packet_complete: this.props.Student.SevenTargetSchools || "",
                coach_final_review: this.props.Student.SevenTargetSchools || "",
                lor: this.props.Student.SevenTargetSchools || "",
                resume: this.props.Student.SevenTargetSchools || "",
                interview: this.props.Student.SevenTargetSchools || "",
                award: 0
            }
        }
    }

    onFieldChange(e, fieldName) {
        return () => {
            let newState = {};
            newState[fieldName] = e.target.value;
            this.setState(newState);
        }

    }

    render() {
        let fieldNames = Object.keys(this.state.student);
        console.log(fieldNames);
        let titles = fieldNames.map(name => {
            let parts = name.split('_');
            return parts.reduce((part1, part2) => `${part1} ${part2}`);
        });
        let index = 0;
        let studentForm = fieldNames.map(fieldName => {
            let labelGroup = <div className="labelGroup">
                <label className="labelTitle">{titles[index]}</label>
                <input type="text" className="studentAttribute" value={this.state[fieldName]} onChange={e => this.onFieldChange(e, fieldName)} key={"n" + index}/>
            </div>
            index++;
            return labelGroup;
        });
        console.log(titles);

        return (
            <div id="newStudentForm">
                {studentForm}
            </div>
            )
        }
}
