import React from 'react';

export default class ContextMenu extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedStudent: this.props.SelectedStudent,
            x: this.props.X,
            y: this.props.Y
        }
    }

    deleteStudent() {
        fetch("/api/delete", this.getAjaxConfig("DELETE"))
            .then(res => res.json())
            .then(res => console.log(res))
    }

    editStudent() {
        fetch("/api/edit", this.getAjaxConfig("PUT"))
            .then(res => res.json())
            .then(res => console.log(res))
    }

    getAjaxConfig(httpMethod) {
        return {
            method:httpMethod,
            body:JSON.stringify(this.state.selectedStudent),
            headers: {
                "Content-Type": "application/json",
                "authorization": "admin"
            }
        }
    }

    render() {
        let css = {
            position:"absolute",
            left:`${this.state.x}px`,
            top:`${this.state.y}px`
        };
        return (
            <div id="context-menu" style={css}>
                <div className="context-menu-item" onClick={this.editStudent.bind(this)}>Change</div>
                <div className="context-menu-item" onClick={this.deleteStudent.bind(this)}>Delete</div>
            </div>
        );

    }


}
