import React from 'react';
import ReactDOM from 'react-dom';
import ContextMenu from './ContextMenu';

export default class StudentList extends React.Component {
    constructor(props) {
        super(props);
        console.log("StudentList Constructor");
        this.state = {
            students: this.props.Students || []
        };

    }

    update() {
        let config = {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "admin"
            }
        };
        fetch('/api/students', config)
            .then(res => res.json())
            .then(res => this.setState({ students: res }));
    }

    componentDidMount() {
        this.update();
    }

    componentDidUpdate(prevStudents) {
        if (this.props.Students.length !== prevStudents.Students.length)
            this.update();
    }

    showContextMenu(student) {
        ReactDOM.render(<ContextMenu SelectedStudent={student}/>, 
            document.getElementById(student.StudentId));
    }

    render() {
        let list = this.state.students.map(student => {
            return <div className="student-list-item" id={student.StudentId}>
                <span className="student-list-item-label">{student.studentId}</span>
                <div className="options-button" onClick={this.showContextMenu.bind(this, student)}>
                    <div></div>
                    <div></div>
                    <div></div>
                </div>
            </div>
        });

        return <div className="student-container">
            {list}
        </div>

    }
}
